Column.value:
/*
* Script Name: * Author: Giu Platania
* Purpose: generate a ProtoBuff File
* Date: 8/28/2020
*/
function main()
{
var schema as EA.SchemaComposer;
var xmlType as EA.SchemaType;

var xmlTypeEnum as EA.SchemaTypeEnum;
var xmlProp as EA.SchemaProperty;
var xmlPropEnum as EA.SchemaPropEnum;
var xmlChoice as EA.SchemaType;
var xmlChoiceEnum as EA.SchemaTypeEnum;
// COnfiguration
// Header
var syntax='syntax = "proto3";';
var optionOptimizeFor = "option optimize_for = LITE_RUNTIME;";
var protobufPackage = "package atakmap.commoncommo.protobuf.v3;	";

Session.Output(syntax);	
Session.Output(optionOptimizeFor);	
Session.Output(protobufPackage);	
// Get SchemaComposer
schema = Repository.SchemaComposer;
// Get Schema Types Enumerator
xmlTypeEnum = schema.SchemaTypes;
// Get total number of types in schema
var count = xmlTypeEnum.GetCount();
// Enumerate Types
var UMLProtoDict = {'boolean': 'bool',
					'int': 'sint32',
					'string': 'string',
					'float': 'float',
					'long': 'sint64',
					'byte': 'bytes',
					'double': 'double'}
xmlType = xmlTypeEnum.GetFirst();
	while(xmlType)
	{
	// Print Class note
		//Session.Output( "// " + xmlType. + " {");
	Session.Output( "Message " + xmlType.TypeName + " {");
	xmlPropEnum = xmlType.Properties;
			var number = 0
	if(xmlPropEnum)
	{
		xmlProp = xmlPropEnum.GetFirst();


		while(xmlProp)
		{
		number = number + 1;
		var sPropDesc = xmlProp.Name;
		sPropDesc += " =" + number;

		// checks if attribute is optional

		if(xmlProp.IsPrimitive())

			sPropDesc = UMLProtoDict[xmlProp.PrimitiveType] + " " +sPropDesc;
		else
			//seems to be triggered on attrib type being a uml object instance
			sPropDesc = xmlProp.TypeName.charAt(0).toUpperCase() + xmlProp.TypeName.substring(1) + " " + sPropDesc;

		if(xmlProp.Cardinality === '0..1')
			sPropDesc = 'optional ' + sPropDesc; 

		if(xmlProp.InitialValue.length>0)
		sPropDesc = sPropDesc + ` [default = ${xmlProp.InitialValue}]`

		if(xmlProp.IsByReference())
			{
				sPropDesc += " (by reference) ";
			}
		if(xmlProp.IsInline())
			{
			sPropDesc += " (inline) ";
			}


			var comment = xmlProp.Annotation.replace(/(\r\n|\n|\r)/gm, "");
			Session.Output( " " + sPropDesc +  ';   // ' + comment);
			xmlChoiceEnum = xmlProp.Choices;
		if(xmlChoiceEnum.GetCount()>0)
			{

			xmlChoice = xmlChoiceEnum.GetFirst();
			Session.Output( " ENUM: ");
			while(xmlChoice)
			{

				Session.Output( " " + xmlChoice.TypeName );
				xmlChoice = xmlChoiceEnum.GetNext();
			}
		}
		xmlProp = xmlPropEnum.GetNext();
		}
	}
		xmlType = xmlTypeEnum.GetNext();
		// close tag
		Session.Output( "}");
	}
			//TODO: change from VB to JS
			//set scriptFile = New TextFile
			//scriptfile.Contents = sqlGetString
			//save the script
			//scriptFile.FullPath = SQLPath & SourceStereotype& "_" & connectortype  & "_" & TargetStereotype & ".sql"
			//scriptFile.Save
			//debug info
			//Session.Output "saving script: " & scriptFile.FullPath

}

main();

/*

* Script Name: Example Schema Composer Script

* Author:   Giu Platania

* Purpose: Generate inserts for FTS tables
* Language: Javascript
* Date:  2019

*/

function printType( xmlType, xmlns, uri)

{

var xmlProp as EA.SchemaProperty;
var xmlPropEnum as EA.SchemaPropEnum;
var xmlChoiceEnum1 as EA.SchemaTypeEnum;
var xmlChoiceEnum2 as EA.SchemaTypeEnum;
xmlPropEnum = xmlType.Properties;

if(xmlPropEnum)

{

xmlProp = xmlPropEnum.GetFirst();

while(xmlProp)

{

if(xmlType.IsEnumeration())

{

Session.Output('	('+"'"+xmlProp.Name+"'"+','+"'"+xmlProp.InitialValue+"'"+')'+',');

}

else

{

var sPropDesc = xmlProp.Name;

sPropDesc += "::"

if(xmlProp.IsPrimitive())

sPropDesc += xmlProp.PrimitiveType;

else

sPropDesc += xmlProp.TypeName;


if(xmlProp.IsByReference())

{

sPropDesc += "(by reference)";

}

if(xmlProp.IsInline())

{

sPropDesc += "(inline)";

}

Session.Output("   " + sPropDesc + ", cardinality: " + xmlProp.Cardinality);


xmlChoiceEnum1 = xmlProp.Choices;

xmlChoiceEnum2 = xmlProp.SchemaChoices;

var count = xmlChoiceEnum1.GetCount() + xmlChoiceEnum2.GetCount();

if(count>1)

{

Session.Output("   choice of: ");

xmlChoice = xmlChoiceEnum1.GetFirst();

while(xmlChoice)

{

Session.Output("     " + xmlChoice.TypeName);

xmlChoice = xmlChoiceEnum1.GetNext();

}

xmlChoice = xmlChoiceEnum2.GetFirst();

while(xmlChoice)

{

Session.Output("     " + xmlChoice.TypeName);

xmlChoice = xmlChoiceEnum2.GetNext();

}

}

}

xmlProp = xmlPropEnum.GetNext();

}

}

}


function main()

{
var schema as EA.SchemaComposer;
var xmlType as EA.SchemaType;
var xmlTypeEnum as EA.SchemaTypeEnum;
var xmlNamespaceEnum as EA.SchemaNamespaceEnum;
var xmlNS as EA.SchemaNamespace;
var tableName = 'Types';
var columns = "('humanreadable', 'CoT')";
var BaseQuery = 'INSERT INTO ' + tableName + " " + columns + " VALUES ";
Session.Output(BaseQuery)
// Get SchemaComposer

schema = Repository.SchemaComposer;

// print the namespace references

xmlNamespaceEnum = schema.Namespaces;

if(xmlNamespaceEnum)

{

xmlNS = xmlNamespaceEnum.GetFirst();

while(xmlNS)

{


xmlNS = xmlNamespaceEnum.GetNext();

}

}

// Get Schema Types Enumerator

xmlTypeEnum = schema.SchemaTypes;
xmlType = xmlTypeEnum.GetFirst();

while(xmlType)

{

var xmlns = schema.GetNamespacePrefixForType( xmlType.TypeID );

uri = schema.GetNamespaceForPrefix(xmlns);

printType(xmlType, xmlns, uri);

xmlType = xmlTypeEnum.GetNext();

}
Session.Output(';')
}


main();

/*
* Script Name: * Author: Giu @ SSNA
* Purpose: generate a OpenAPI file in YAML format
* Date: 11/05/2020
* Version 0.1 DRAFT
*/
function main()
{
var schema as EA.SchemaComposer;
var xmlType as EA.SchemaType;

var xmlTypeEnum as EA.SchemaTypeEnum;
var xmlProp as EA.SchemaProperty;
var xmlPropEnum as EA.SchemaPropEnum;
var xmlChoice as EA.SchemaType;
var xmlChoiceEnum as EA.SchemaTypeEnum;
var transformationTitle as EA.SchemaComposer.SchemaName;	
// COnfiguration
var openAPITitel= 'title: ';	
var openAPIVersion= ' version: 3.0.1';	
var	openAPIUrl= "url:";
var	openAPIparameters= "parameters:"; 	
var openAPIdescription = "  description: ";
var openAPIoperationId = " operationId:";
var openAPIcontent = "      content:";
var openAPIParmName = "- name";
var openAPIparmtype = "type: ";
var openAPI2 = "";
var openAPI3 = "";	

// Header
var title= openAPITitel + transformationTitle;



Session.Output(title);		
Session.Output(openAPIVersion);	
//Session.Output();	
//Session.Output();	
// Get SchemaComposer
schema = Repository.SchemaComposer;
// Get Schema Types Enumerator
xmlTypeEnum = schema.SchemaTypes;
// Get total number of types in schema
var count = xmlTypeEnum.GetCount();
// Enumerate Types
var UMLProtoDict = {'boolean': 'bool',
					'int': 'int32',
					'str': 'string',
					'float': 'float',
					'long': 'int64',
					'byte': 'bytes',
					'double': 'double'}
xmlType = xmlTypeEnum.GetFirst();
	while(xmlType)
	{
	// Print Class note
		//Session.Output( "// " + xmlType. + " {");
	Session.Output( xmlType.TypeName );
	Session.Output( openAPIparameters);
	xmlPropEnum = xmlType.Properties;
			var number = 0
	if(xmlPropEnum)
	{
		xmlProp = xmlPropEnum.GetFirst();


		while(xmlProp)
		{
		number = number + 1;
		var sPropDesc = xmlProp.Name;

		// checks if attribute is optional

		if(xmlProp.IsPrimitive())

			sPropDesc = openAPIparmtype + UMLProtoDict[xmlProp.PrimitiveType] ;
		else
			//seems to be triggered on attrib type being a uml object instance
			sPropDesc = xmlProp.TypeName.charAt(0).toUpperCase() + xmlProp.TypeName.substring(1);

		if(xmlProp.Cardinality === '0..1')
			sPropDesc = 'optional ' + sPropDesc; 

		if(xmlProp.InitialValue.length>0)
		sPropDesc = sPropDesc + ` [default = ${xmlProp.InitialValue}]`

		if(xmlProp.IsByReference())
			{
				sPropDesc += " (by reference) ";
			}
		if(xmlProp.IsInline())
			{
			sPropDesc += " (inline) ";
			}


			var comment = xmlProp.Annotation.replace(/(\r\n|\n|\r)/gm, "");
			openAPIdescription
			Session.Output( " " + sPropDesc);
			Session.Output( openAPIdescription + comment);

			xmlChoiceEnum = xmlProp.Choices;
		if(xmlChoiceEnum.GetCount()>0)
			{

			xmlChoice = xmlChoiceEnum.GetFirst();
			Session.Output( " ENUM: ");
			while(xmlChoice)
			{

				Session.Output( " " + xmlChoice.TypeName );
				xmlChoice = xmlChoiceEnum.GetNext();
			}
		}
		xmlProp = xmlPropEnum.GetNext();
		}
	}
		xmlType = xmlTypeEnum.GetNext();
		// close tag
		Session.Output( "");
	}

}

main();

// Script Name:     SetStereotypeOnSelectedElements
// Author:          Giu Platania
// Purpose:         Updates the stereotype for all elements selected in the Project Browser.
// Date:            2025-08-06
// Version:         1.0

!INC Local Scripts.EAConstants-JScript
!INC EAScriptLib.JScript-Dialog

/*
* Main function for Project Browser Script.
* Select elements in the Project Browser, then execute the script to set a new stereotype.
*/
function OnProjectBrowserScript()
{
    // Show the script output window
    Repository.EnsureOutputVisible("Script");

    // Prompt user for the new stereotype
    var input = DLGInputBox('Enter new stereotype', 'Multi-select element update', '');
    if (input == null || input == "") {
        Session.Output('No stereotype entered. Operation cancelled.');
        return;
    }

    // Get the currently selected elements in the Project Browser
    var selElem = Repository.GetTreeSelectedElements();

    if (selElem == null || selElem.Count == 0) {
        Session.Output('No elements selected. Please select elements in the Project Browser.');
        return;
    }

    // Update stereotype for each selected element
    for (var i = 0; i < selElem.Count; i++) {
        var e = selElem.GetAt(i);
        var oldStereotype = e.Stereotype;
        e.Stereotype = input;
        e.Update();
        e.Refresh();
        Session.Output('Element changed: ' + e.Name + ' (old stereotype: ' + oldStereotype + ') changed stereotype to: ' + e.Stereotype);
    }
    Session.Output('All selected elements updated.');
}

OnProjectBrowserScript();

!INC Local Scripts.EAConstants-JScript
// Replace the file below with another element list to reuse this script.
//!INC Conversion Scripts.ConversionTable

function TypeConversion(sourceObject,sourceStereotype,targetObject, targetStereotype)
{
    this.sourceObject = sourceObject;
    this.sourceStereotype = sourceStereotype;
    this.targetObject = targetObject;
    this.targetStereotype = targetStereotype;
}

var Conversions = new Array();

//Convert standards to DAF standard
Conversions[0] = new TypeConversion("Activity", "dPhysicalService", "Interface", "dPhysicalService");
//Conversions[0] = new TypeConversion("Class", "", "Class", "TMF_BusinessEntity");
// =================================================================================
// Name: Element Conversion
// Converts elements type and stereotype as defined in the element conversation table.
// The table is included as a file so it may be changed for other mappings
// Navigates from selected package and recursively modifies each element
// NOTE: Requires a package to be selected in the Project Browser
//
// Related APIs
// =================================================================================
// Element API - http://www.sparxsystems.com/enterprise_architect_user_guide/10/automation_and_scripting/element2.html
// Repository API http://www.sparxsystems.com/enterprise_architect_user_guide/10/automation_and_scripting/repository3.html
// Tagged Value API http://www.sparxsystems.com/enterprise_architect_user_guide/10/automation_and_scripting/taggedvalue.html

function StartWithSelectedPackage()
{
    // Show the script output window
    Repository.EnsureOutputVisible( "Script" );

    Session.Output( "JScript TypeConverstion" );
    Session.Output( "===========================" );

    var thePackage as EA.Package;
    thePackage = Repository.GetTreeSelectedPackage();

    if ( thePackage != null && thePackage.ParentID != 0 )
    {
        NavigatePackage( "", thePackage );
    }
    else
    {
        Session.Prompt( "This script requires a package to be selected in the Project Browser.\n" +
            "Please select a package in the Project Browser and try again.", promptOK );
    }

    Session.Output( "Done!" );
}

//
// Outputs the packages name and elements, and then recursively processes any child
// packages
//
// Parameters:
// - indent A string representing the current level of indentation
// - thePackage The package object to be processed
//
function NavigatePackage( indent, thePackage )
{
    // Cast thePackage to EA.Package so we get intellisense
    var currentPackage as EA.Package;
    currentPackage = thePackage;

    // Add the current package's name to the list
    Session.Output( indent + currentPackage.Name + " (PackageID=" +
        currentPackage.PackageID + ")" );

    // Convert the elements this package contains
    ConvertElementsInPackage( indent + "    ", currentPackage );

    // Recursively process any child packages
    var childPackageEnumerator = new Enumerator( currentPackage.Packages );
    while ( !childPackageEnumerator.atEnd() )
    {
        var childPackage as EA.Package;
        childPackage = childPackageEnumerator.item();
        NavigatePackage( indent + "    ", childPackage );

        childPackageEnumerator.moveNext();
    }
}

//
// Converts the elements of the provided package to the Script output window
//
// Parameters:
// - indent A string representing the current level of indentation
// - thePackage The package object to be processed
//
function ConvertElementsInPackage( indent, thePackage )
{
    // Cast thePackage to EA.Package so we get intellisense
    var currentPackage as EA.Package;
    currentPackage = thePackage;

    // Iterate through all elements and add them to the list
    var elementEnumerator = new Enumerator( currentPackage.Elements );
    while ( !elementEnumerator.atEnd() )
    {
        var currentElement as EA.Element;
        currentElement = elementEnumerator.item();
        ConvertElements(indent+"    ",currentElement );

        elementEnumerator.moveNext();
    }
}

function ConvertElements( indent, theElement )
{
    // Cast theElement to EA.Element so we get intellisense
    var currentElement as EA.Element;
    currentElement = theElement;
    currentElement.ObjectType
    ConvertElement(indent+"    ",currentElement );
    // Iterate through all embedded elements and add them to the list
    var elementEnumerator = new Enumerator( currentElement.Elements );
    while ( !elementEnumerator.atEnd() )
    {
        var currentElement as EA.Element;
        currentElement = elementEnumerator.item();
        ConvertElements(indent+"    ",currentElement );
        elementEnumerator.moveNext();
    }
}

// Converts the element from BSIF to
//
// Parameters:
// - indent A string representing the current level of indentation
// - theElement The element object to be processed
function ConvertElement( indent, theElement )
{
// Debug Comment out when run for real
    //Session.Output( indent + "CALLED: ConvertElement with " + theElement.Name + " [" + theElement.Type + ", " + theElement.Stereotype + " )" );

    for ( var i = 0 ; i < Conversions.length ; i++ )
    {
        // If want to limit to stereotype that matches source list then convert
 //       if ( (theElement.Stereotype == Conversions[i].sourceStereotype) && (theElement.Type == Conversions[i].sourceObject ))
        {
            Session.Output( indent + "CONVERTED: " + theElement.Name + " (" + theElement.Type + ", " + theElement.Stereotype + ")" + "=>" +"("+Conversions[i].targetObject+","+Conversions[i].targetStereotype+")" );
             theElement.Type = Conversions[i].targetObject;
            //Overright the stereotype list to have only one stereotype
            theElement.StereotypeEx = Conversions[i].targetStereotype;
            theElement.Update();
            break; // once found cease iterating through for-loop.
        }
    }
}

StartWithSelectedPackage();

!INC Local Scripts.EAConstants-JScript

