!INC Local Scripts.EAConstants-JScript

/**
 * Helper: write text to disk via Windows FileSystemObject
 */
function writeViaActiveX(path, text) {
    var fso = new ActiveXObject("Scripting.FileSystemObject");
    var a = fso.CreateTextFile(path, true);
    a.WriteLine(text);
    a.Close();
    Session.Output("Wrote " + text.length + " chars to " + path);
}

// JSON2 polyfill (Douglas Crockford)
if (!this.JSON) { JSON = {}; }
(function () {
    function f(n) { return n < 10 ? '0' + n : n; }
    if (typeof Date.prototype.toJSON !== 'function') {
        Date.prototype.toJSON = function () {
            return isFinite(this.valueOf()) ?
                this.getUTCFullYear() + '-' + f(this.getUTCMonth() + 1) + '-' +
                f(this.getUTCDate()) + 'T' + f(this.getUTCHours()) + ':' +
                f(this.getUTCMinutes()) + ':' + f(this.getUTCSeconds()) + 'Z' : null;
        };
        String.prototype.toJSON = Number.prototype.toJSON = Boolean.prototype.toJSON = function () { return this.valueOf(); };
    }
    var escapable = /[\\"\u0000-\u001f]/g,
        meta = { '\b':'\\b','\t':'\\t','\n':'\\n','\f':'\\f','\r':'\\r','"':'\\"','\\':'\\\\' };
    function quote(string) {
        escapable.lastIndex = 0;
        return escapable.test(string)
            ? '"' + string.replace(escapable, function(a) {
                  var c = meta[a];
                  return typeof c == 'string' ? c :
                      '\\u' + ('0000' + a.charCodeAt(0).toString(16)).substring(a.charCodeAt(0).toString(16).length - 4);
              }) + '"'
            : '"' + string + '"';
    }
    function str(key, holder) {
        var i, v, value = holder[key];
        if (value && typeof value === 'object' && typeof value.toJSON === 'function') {
            value = value.toJSON(key);
        }
        switch (typeof value) {
            case 'string': return quote(value);
            case 'number': return isFinite(value) ? String(value) : 'null';
            case 'boolean':
            case 'null': return String(value);
            case 'object':
                if (!value) return 'null';
                var partial = [];
                if (Object.prototype.toString.apply(value) === '[object Array]') {
                    for (i = 0; i < value.length; i++) {
                        partial[i] = str(i, value) || 'null';
                    }
                    return '[' + partial.join(',') + ']';
                }
                for (var k in value) {
                    if (Object.prototype.hasOwnProperty.call(value, k)) {
                        v = str(k, value);
                        if (v) partial.push(quote(k) + ':' + v);
                    }
                }
                return '{' + partial.join(',') + '}';
        }
    }
    JSON.stringify = function (value, replacer, space) {
        return str('', {'': value});
    };
}());

function main() {
    var proj = Repository.GetProjectInterface();
    // Prompt user for output file
    var path = proj.GetFileNameDialog(
        "OpenAPI.json",
        "JSON Files (*.json)|*.json|All Files (*.*)|*.*",
        1, // Index of the default filter (Save dialog mode if SaveOrOpen=2)
        0, // Flags
        "", // Initial directory
        2  // OpenOrSave: 1=Open, 2=Save
    );
    if (!path) {
        Session.Output("No file selected, aborting.");
        return;
    }

    // Build OpenAPI object
    var schema = Repository.SchemaComposer;
    var title = schema.SchemaName;
    var version = "3.0.1";
    var UMLProtoDict = { 'boolean':'boolean','int':'integer','str':'string','float':'number','long':'integer','byte':'string','double':'number' };
    var openAPI = { openapi:version, info:{title:title,version:version}, paths:{}, components:{schemas:{}} };

    var xmlType = schema.SchemaTypes.GetFirst();
    while (xmlType) {
        var name = xmlType.TypeName.charAt(0).toUpperCase() + xmlType.TypeName.substring(1);
        var def = { type:"object", properties:{} };
        var props = xmlType.Properties;
        if (props) {
            var p = props.GetFirst();
            while (p) {
                var pd = {};
                if (p.IsPrimitive()) pd.type = UMLProtoDict[p.PrimitiveType] || 'string';
                else pd.$ref = "#/components/schemas/" + p.TypeName.charAt(0).toUpperCase() + p.TypeName.substring(1);
                if (p.Cardinality == '0..1') pd.nullable = true;
                if (p.InitialValue) pd["default"] = p.InitialValue;
                var choices = p.Choices;
                if (choices && choices.GetCount() > 0) {
                    var ev = [], c = choices.GetFirst(); while(c) { ev.push(c.TypeName); c = choices.GetNext(); }
                    pd["enum"] = ev;
                }
                def.properties[p.Name] = pd;
                p = props.GetNext();
            }
        }
        openAPI.components.schemas[name] = def;
        xmlType = schema.SchemaTypes.GetNext();
    }

    // Serialize and write via ActiveX
    var jsonText = JSON.stringify(openAPI, null, 2);
    writeViaActiveX(path, jsonText);
}

main();

/*
 * Script Name: GenerateJsonSchema.js
 * Author: Giu Platania
 * Purpose: Generate a complete JSON Schema using EA.SchemaComposer
 * Date: 11/05/2020
 * Version: 2.0 (JScript)
