!INC EAScriptLib.JavaScript-Database
/*
 * Script Name: 
 * Author: 
 * Purpose: 
 * Date: 
 */
 var repo as EA.Repository;
repo = Repository;
function main()
{

	var diagramPackage as EA.Package;
	diagramPackage = repo.GetPackageByGuid("{D5259ADD-8012-4f18-8735-F8DFA3EF2C57}");

	var xmlContent = repo.SQLQuery("SELECT SourceElement.Object_ID as 'source_guid', TargetElement.Object_ID AS 'target_guid', conn.Connector_ID AS 'conn_ID'  FROM t_object AS TargetElement     INNER JOIN  	t_connector AS conn ON conn.End_Object_ID = TargetElement.Object_ID    INNER JOIN  	t_object AS SourceElement ON conn.Start_Object_ID = SourceElement.Object_ID    Inner Join  	t_connectortag Assessed ON (conn.Connector_ID = Assessed.ElementID  and Assessed.Property='Assessed')   Inner Join  	t_connectortag confidence ON (conn.Connector_ID = confidence.ElementID  and confidence.Property='confidence')   	 Inner Join  	t_connectortag TargetStereotype  	ON (conn.Connector_ID = TargetStereotype.ElementID  and TargetStereotype.Property='TargetStereotype')  	WHERE conn.Stereotype = 'TMF_CandidateRelationship'   order by SourceElement.ea_guid DESC")                                                 

	Session.Output(xmlContent)
	var xmlDOM = new COMObject("MSXML2.DOMDocument");
	xmlDOM.validateOnParse = false;
	xmlDOM.async = false;
	var doc = xmlDOM.loadXML(xmlContent)

	Session.Output(doc)
	Session.Output("running")

	let elements = []

	// Get GUIDs of all elements
	let rowNodes = xmlDOM.documentElement.selectNodes("Dataset_0/Data/Row");

	for (let i = 0; i < rowNodes.length; i++) {
		let sourceGuid = rowNodes.item(i).childNodes.item(0).text;
		let targetGuid = rowNodes.item(i).childNodes.item(1).text;
		let relID = parseInt(rowNodes.item(i).childNodes.item(2).text);

		if (targetGuid !== null && sourceGuid !== null) {
			elements.push([sourceGuid, targetGuid, relID]);
		}
	}

	// entry: [diagram, index, visibleLinks, leftCount]
	var diagrams = [];

	let diagramIndex = 0;

	let diagram = diagramPackage.Diagrams.AddNew("Relationship Diagram Group 0 Page 0", "");

	diagramPackage.Update();
	diagram.Update();

	diagrams.push([[diagram], 0, [], 10]);

	// loop element sets
	for (let i=0; i<elements.length; i++) 
	{
		diagramIndex = 0;
		let elementSet = elements[i];

		// get the current diagram set
		let diagramSet = getDiagram(diagramPackage, diagrams, diagramIndex);

		// iterate down the diagram list until a diagram without either element is found
		// pass the current element set and the last page
		while (diagramContainsElements(elementSet, diagramSet[0][diagramSet[0].length-1]))
		{
			diagramIndex += 1;
			diagramSet = getDiagram(diagramPackage, diagrams, diagramIndex);
		}
		// add the element set to the diagram set
		addElementSetToDiagram(diagramSet, elementSet);
	}
	diagramPackage.Update()
	// iterate all diagram sets
	for (let i=0; i<diagrams.length; i++)
	{
		// get the diagramPage list
		let diagramPages = diagrams[i][0];
		// iterate the diagram pages
		for (let n=0; n<diagramPages.length; n++)
		{
			//get the diagram page
			let diagram = diagramPages[n];
			// get the visible links list
			let visibleLinks = diagrams[i][2];

			diagram.Update()
			diagram.DiagramLinks.Refresh();

			// iterate all diagram links on page
			for (let n = 0; n<diagram.DiagramLinks.Count; n++)
			{
				// get link n
				let link = diagram.DiagramLinks.GetAt(n);
				// check if link n is in the visibleLinks list
				let index = visibleLinks.indexOf(link.ConnectorID);
				// if link n is not in visibleLinks hide it
				if (index == -1)
				{
					link.IsHidden = true;
					link.Update();
					diagram.DiagramLinks.Refresh()
				}
				// otherwise remove it from the list
				//else
				//{
				//	visibleLinks.splice(index, 1);
				//}
			}
			// update the diagram
			diagram.Update();
		}
	}
}

// add a set of elements to a diagram set. the element set contains source and target element guids,
// the diagram set has the current index of the diagram and the diagram object. 
function addElementSetToDiagram(diagramSet, elementSet){
	// get last diagram page
	let diagram = diagramSet[0][diagramSet[0].length-1];
	// get current index
	let index = diagramSet[1];
	// get the left starting point
	let leftSide = diagramSet[3];
	// add the rel ID to the visible rels
	diagramSet[2].push(elementSet[2]);

	// add the source element to the diagram
	// var sourceDiagramObj as EA.DiagramObject;
	let dimensions = calculateElemVals(90, 70, index, leftSide, 100, 130, 4);
	let top = dimensions[0];
	let bottom = dimensions[1];
	let left = dimensions[2];
	let right = dimensions[3];
	let sourceElem = addElementToDiagram(diagram, elementSet[0], top, bottom, left, right);

	// add the target element to the diagram
	dimensions = calculateElemVals(90, 70, index+1, dimensions[3], 100, 130, 4);
	top = dimensions[0];
	bottom = dimensions[1];
	// add 5 pixels per character in relationship name between related elements
	let link = repo.GetConnectorByID(elementSet[2])
	let linkName = link.Name
	left = dimensions[2] + (linkName.length*3);
	diagramSet[3] = dimensions[2] + (linkName.length*3);
	right = dimensions[3] + (linkName.length*3);
	let targetElem = addElementToDiagram(diagram, elementSet[1], top, bottom, left, right);
	let newLink = diagram.DiagramLinks.AddNew("", "")
	newLink.ConnectorID = link.ConnectorID;
	newLink.Update()
	Session.Output(diagram.DiagramLinks.GetLastError())

	// update both diagrams
	let sourceElemUpdated = sourceElem.Update();
	let targetElemUpdated = targetElem.Update();
	let linkUpdated = link.Update();
	diagram.DiagramLinks.Refresh()
	let diagramUpdated = diagram.Update();

	// update the diagram index
	diagramSet[1] += 2;
}

// check if the diagram contains the given element
function diagramContainsElements(elements, diagram)
{
	diagram.DiagramObjects.Refresh();
	for (let i=0; i<diagram.DiagramObjects.Count; i++)
	{
		for (let n=0; n<elements.length; n++){

			if (diagram.DiagramObjects.GetAt(i).ElementID == elements[n]){
				return true;
			}
		}
	}
	return false;
}

function getDiagram(diagramPackage, diagrams, index)
{
	//var diagrams as Array
	// check if the diagrams list is long enough
	// if it isn't long enough create a new diagram
	if (diagrams.length-1<index){
		var diagram as EA.Diagram;
		diagram = diagramPackage.Diagrams.AddNew("Relationship Diagram Group "+index+" Page 0", "Class");

		diagramPackage.Update();
		diagram.Update();
		// make a diagram set with a single page, a 0 index, no visible relations and a left starting point of 10
		diagrams.push([[diagram], 0, [], 10]);
	}
	// access the diagram set at the specified index
	let diagramSet = diagrams[index];
	// if the diagram set index is greater than 14 add another page
	if (diagramSet[1] > 10){
		var diagram as EA.Diagram;
		diagram = diagramPackage.Diagrams.AddNew("Relationship Diagram Group "+index+" Page "+diagramSet[0].length, "Class");

		diagramPackage.Update();
		diagram.Update();
		// push new page
		diagramSet[0].push(diagram);
		// set the index to 0
		diagramSet[1] = 0;
		// set the left starting point to 10
		diagramSet[3] = 10;
	};
	return diagrams[index];
}

function calculateElemVals(width, height, index, prevLeft, verticalSpace, horizontalSpace, elementsPerRow)
{
	let topVal = (((index-(index%elementsPerRow))/elementsPerRow)*(-verticalSpace))-10;
	let bottomVal = topVal-height;
	let leftVal = 10;
	if (index%elementsPerRow != 0){
		leftVal = prevLeft+horizontalSpace;
	}
	let rightVal = leftVal+width;
	return [topVal, bottomVal, leftVal, rightVal];
}

function addElementToDiagram(diagram, elementID, top, bottom, left, right)
{
	// var diagramObj as EA.DiagramObject
	diagram.DiagramObjects.Refresh();
	let diagramObj = diagram.DiagramObjects.AddNew(elementID, "Class");
	diagramObj.ElementID = elementID;
	diagramObj.top = top;
	diagramObj.bottom = bottom;
	diagramObj.left = left;
	diagramObj.right = right;
	let diagramObjUpdated = diagramObj.Update();
	return diagramObj;
}

main();

