function approveRelationship(candidateRelationship)
{
	candidateRelationship.StereotypeEx = "TMF::"+candidateRelationship.TaggedValues.GetAt(2).Value;
	Session.Output("set rel stereo to "+candidateRelationship.StereotypeEx);
	candidateRelationship.Type = candidateRelationship.TaggedValues.GetAt(3).Value;
	Session.Output("set rel type to "+candidateRelationship.Type);
	//selection.ObjectType = selection.TaggedValues.GetAt(3).Value;
	Session.Output("rel update success "+candidateRelationship.Update());
	var client = Repository.GetElementByID(candidateRelationship.ClientID);
	Session.Output("client update success "+client.Update());
	var supplier = Repository.GetElementByID(candidateRelationship.SupplierID);
	Session.Output("supplier update success "+supplier.Update());
	supplier.Connectors.Refresh();
	client.Connectors.Refresh();
}

function rejectRelationship(candidateRelationship)
{
	var client = Repository.GetElementByID(candidateRelationship.ClientID)
	for (n=0; n<client.Connectors.Count; n++){
		if (client.Connectors.GetAt(n).Name == selection.Name){
			client.Connectors.Delete(n)
			client.Update()
			client.Connectors.Refresh()
		}
	}
}

