/*
 * Script Name: ODA - Extract Messages From IIC
 * Author: Natha
 * Purpose: WIP, this script will extract all the messages from a ISC and create the sequence diagram automatically. WIP
 * Date: 
 */

 var Repository as EA.Repository;

function main(elementID, DiagramType)
{
	var IIC as EA.Element;
	IIC = Repository.GetElementByID(elementID);

	var ISC as EA.Element;
	ISC = getISCFromIIC(IIC);

	var diagrams as EA.Collection;
	diagrams = ISC.Diagrams;

	var xmlOutput = '<messages>';

	for (o=0; o<diagrams.Count; o++)
	{
		var diagram as EA.Diagram;
		diagram = diagrams.GetAt(o);
		xmlOutput+=getMessageNamesFromDiagram(diagram, xmlOutput);
	}
	xmlOutput+='</messages>'
	Session.Output(xmlOutput)


}

function getMessageNamesFromDiagram(diagram){
	var xmlContent = "";

	for (l=0; l<diagram.DiagramLinks.Count; l++){
		var link as EA.DiagramLink;
		link = diagram.DiagramLinks.GetAt(l);

		var connector as EA.Connector;
		connector = Repository.GetConnectorByID(link.ConnectorID);

		if (connector.Type == 'Sequence'){
			xmlContent+="<message>"+connector.Name+"</message>"
			Session.Output(connector.Name);
		}

	}
	return xmlContent;

}

function formatMessageName(name){
	name = name.split("()")[0]
	return name.split("(?<!(^|[A-Z]))(?=[A-Z])|(?<!^)(?=[A-Z][a-z])")
}

function getISCFromIIC(IIC){
	for (n=0; n<IIC.Elements.Count; n++){
		var child as EA.Element;
		child = IIC.Elements.GetAt(n);

		if (child.Stereotype == 'TMF_ISC'){
			return child;
		}
	}
}

