!INC Local Scripts.EAConstants-JScript
/*
 * Script Name: 	Populate Note with Tagged Values
 * Author:			Nick Hoggard
 * Purpose:			Extracts the Tagged Value (Name/Value) pairs from the Eement
 * 					(or Connector) associated with the Note, and replaces the note 
 * 					content with a representation of these values.
 * Date:			16/06/2018
 * Sparx Version:	13.5
 */

function main()
{
	Repository.EnsureOutputVisible( "Script" );

	var selectedObject as EA.Element;
	var connector as EA.Connector;
	var associatedElement as EA.Element;
	var taggedValues as EA.Collection;
	var connectorID;

	selectedObject = Repository.GetContextObject();
	if (selectedObject.Type == "Note") 
	{	

		// The note might be connected to either an Element, a Connector, Nothing, or More than 1 object
		// Connecting to nothing, or to more than one object is not supported
		// The connection between the note and the element must originate (ClientID) at the note and terminate (SupplierID)
		// on the target object.
		if (selectedObject.Connectors.Count == 0 && selectedObject.MiscData(3) != "") {
			// Sparx (13.5) stores a connection from a Note to a Connector in PDATA4 on the element record
			// 	This is accessed via the MiscData(3) property.
			//  This is one of the aspects of this script that might not survive a Sparx version upgrade.
			// PDATA 4 is stored as "idref1=ID;", so the ID has to be extracted before we find the connector
			connectorID = selectedObject.MiscData(3). replace("idref1=","").replace(";","");
			connector = Repository.GetConnectorByID(connectorID);
			taggedValues = connector.TaggedValues;
		} else if (selectedObject.Connectors.Count == 1) {
				Session.Output ("connector found! " + selectedObject.Connectors.Count); 			
			// If there is an object in the Connectors collection, this means the note is associated with an element.
			associatedElement = Repository.GetElementByID(selectedObject.Connectors.GetAt(0).SupplierID);
			Session.Output ("element found! " + associatedElement.name);
			taggedValues = associatedElement.TaggedValues;
		} else { 
			Session.Output ("Incorrect number of connectors" + selectedObject.Connectors.Count); 
			return;
		}		

		var output = "<u><b>TMF Properties</b></u>\r\n";	
		for ( var i = 0 ; i < taggedValues.Count ; i++ )
		{
			var tv as EA.TaggedValue
			output = output + "<b>" + taggedValues.GetAt(i).Name + "</b>  : " + taggedValues.GetAt(i).Value + "\r\n";
		}	

		// Again, this is likely to be unsupported, but I like the "Memo" style for the notes
		selectedObject.StyleEx="NS=1;";
		selectedObject.Notes = output;
		selectedObject.Update();
	}
	else
	{
		Session.Output ("Incorrect element type, need a Note"); 
		}
}


main();

!INC Local Scripts.EAConstants-JavaScript

/*
 *
 * Script Name: Elements -> Set Stereotype To Rectangle style
 * Author: Giu Platania
 * Purpose: set all the elements of a certain stereotype to the rectangle syle
 * Date: 2023-12.04
 */

/*
 * Diagram Script main function
 */

// constants
// the target stereotype for style modification
TARGET_STEREOTYPE = "TMF_Process";

// 0 to remove rect 1 to use rect
SET_RECT = 0;

var repo as EA.Repository;
repo = Repository;

function OnDiagramScript()
{
	// Get a reference to the current diagram
	var currentDiagram as EA.Diagram;
	currentDiagram = Repository.GetCurrentDiagram();
	//ONLY FOR DEBUGGING!
	if (currentDiagram == null){
		currentDiagram = Repository.GetDiagramByGuid("{BD6B5062-3B7E-41cf-93E8-E9332DBB13B8}")
	}
	if ( currentDiagram != null )
	{
		/*
		var query as String;
		query = "SELECT t_diagramobjects.ObjectStyle FROM t_diagramobjects join t_diagram on t_diagram.Diagram_ID = t_diagramobjects.Diagram_ID join t_object on t_diagramobjects.Object_ID = t_object.Object_ID where t_diagram.ea_guid = '" + currentDiagram.DiagramGUID + "' and t_object.Stereotype = '" + TARGET_STEREOTYPE + "';";                            

		var xmlContent as String;
		var xmlContent = repo.SQLQuery(query);

		var xmlDOM = new COMObject("MSXML2.DOMDocument");
		xmlDOM.validateOnParse = false;
		xmlDOM.async = false;
		var doc = xmlDOM.loadXML(xmlContent)
		*/
		for (let i = 0; i < currentDiagram.DiagramObjects.Count; i++) {
			let currDiagramObj = currentDiagram.DiagramObjects.GetAt(i);
			let currObj = Repository.GetElementByID(currDiagramObj.ElementID);
			if (currObj.Stereotype != TARGET_STEREOTYPE){
				continue;
			}
			//var style as String;
			let style = currDiagramObj.Style;
			// Check if UCRect exists in the input string
			if (style.includes("UCRect")) {
				// If UCRect exists, replace its value with 0
				style = style.replace(/UCRect=[01]/, 'UCRect='+SET_RECT);
			} else {
				// If UCRect doesn't exist, add it to the end with a value of 0
				style += ";UCRect="+SET_RECT+";";
			}
			currDiagramObj.Style = style;
			currDiagramObj.Update();
			currObj.Update();
		}
		currentDiagram.Update();
		Repository.ReloadDiagram(currentDiagram.DiagramID);
	}
	else
	{
		Session.Prompt( "This script requires a diagram to be visible.", promptOK)
	}
}

OnDiagramScript();

