/*
 * This code has been included from the default Diagram Script template.
 * If you wish to modify this template, it is located in the Config\Script Templates
 * directory of your EA install path.
 *
 * Script Name:
 * Author:
 * Purpose:
 * Date:
 */

/*
 * Diagram Script main function
 */
function OnDiagramScript()
{
	// Get a reference to the current diagram
	var currentDiagram as EA.Diagram;
	currentDiagram = Repository.GetCurrentDiagram();

	if ( currentDiagram != null )
	{
		// Get a reference to any selected connector/objects
		var selectedConnector as EA.Connector;
		var selectedObjects as EA.Collection;
		selectedConnector = currentDiagram.SelectedConnector;
		selectedObjects = currentDiagram.SelectedObjects;

		if ( selectedConnector != null )
		{
			approveRelationship(selectedConnector);
			Repository.ReloadDiagram(currentDiagram.DiagramID);
		}
		else
		{
			Session.Prompt( "You must select a connector to be approved.", promptOK)
		}
	}
	else
	{
		Session.Prompt( "This script requires a diagram to be visible.", promptOK)
	}
}

OnDiagramScript();

!INC Local Scripts.EAConstants-JavaScript

/*
 * This code has been included from the default Diagram Script template.
 * If you wish to modify this template, it is located in the Config\Script Templates
 * directory of your EA install path.
 *
 * Script Name:
 * Author:
 * Purpose:
 * Date:
 */

/*
 * Diagram Script main function
 */
function OnDiagramScript()
{
	// Get a reference to the current diagram
	var currentDiagram as EA.Diagram;
	currentDiagram = Repository.GetCurrentDiagram();

	if ( currentDiagram != null )
	{
		// Get a reference to any selected connector/objects
		var selectedConnector as EA.Connector;
		var selectedObjects as EA.Collection;
		selectedConnector = currentDiagram.SelectedConnector;
		selectedObjects = currentDiagram.SelectedObjects;

		if ( selectedConnector != null )
		{
			var client = Repository.GetElementByID(selectedConnector.ClientID)
			for (n=0; n<client.Connectors.Count; n++){
				if (client.Connectors.GetAt(n).Name == selectedConnector.Name){
					client.Connectors.Delete(n)
					client.Update()
					client.Connectors.Refresh()
					break
				}
			}
			Repository.ReloadDiagram(currentDiagram.DiagramID);
		}
		else
		{
			Session.Prompt( "You must select a connector to be rejected.", promptOK)
		}
	}
	else
	{
		Session.Prompt( "This script requires a diagram to be visible.", promptOK)
	}
}

OnDiagramScript();

!INC Local Scripts.EAConstants-JavaScript

/*
 * Script Name: Show Candidate In Diagram
 * Author: Natha
 * Purpose: jump to the diagram (if any) where a candidate relationship is displayed also select the relationship
 * Date: 
 */

function main()
{
	let diagramPackageGuid = "{D5259ADD-8012-4f18-8735-F8DFA3EF2C57}";
	if (Repository.CurrentSelection.List.Count>1){
		Session.Prompt("only one element can be shown at a time, only select one relation", promptOK);
		return
	}

	let relGuid = Repository.CurrentSelection.List.GetAt(0).ElementGUID;

	Session.Output(relGuid)

	let selectedRel = Repository.GetConnectorByGuid(relGuid);

	let selectedRelID = selectedRel.ConnectorID;

	var query = "SELECT t_diagramlinks.DiagramID FROM t_diagram JOIN t_diagramlinks ON (t_diagram.Diagram_ID = t_diagramlinks.DiagramID) JOIN t_connector ON (t_connector.Connector_ID = t_diagramlinks.ConnectorID) JOIN t_package ON (t_diagram.Package_ID = t_package.Package_ID) WHERE t_diagramlinks.Hidden = 0 and t_package.ea_guid = '"+ diagramPackageGuid + "' AND t_diagramlinks.ConnectorID = "+selectedRelID+";"

	Session.Output(query)
	var xmlContent = Repository.SQLQuery(query)
	Session.Output("Result of query:")
	Session.Output(xmlContent)


	try
	{
	var xmlDOM = new COMObject("MSXML2.DOMDocument");
	xmlDOM.validateOnParse = false;
	xmlDOM.async = false;
	var doc = xmlDOM.loadXML(xmlContent)
	let rowNodes = xmlDOM.documentElement.selectNodes("Dataset_0/Data/Row");
	let diagramID = rowNodes.item(0).childNodes.item(0).text;
	let targetDiagram = Repository.GetDiagramByID(diagramID);
	Repository.OpenDiagram(diagramID);
	targetDiagram.SelectedConnector = selectedRel;
	Repository.RefreshOpenDiagrams(false)
	}
	catch (e) 
	{
		Session.Prompt("this relationship is not displayed in any diagram,you may need to generate the candidate diagrams again", promptOK);
		Session.Output("Exception is:"  + e.message, promptOK)
	}
}

main();

