/*
 * Script Name:  Reject Relationship
 * Author: Giu Platania
 * Purpose: part of the MODA relationship manager, allows to delete  a candidate relationship
 * Date: 2023 12
 */


function main()
{
	for (i=0; i<Repository.CurrentSelection.List.Count; i++){
		var elemGuid = Repository.CurrentSelection.List.GetAt(i).ElementGUID;
		Session.Output(elemGuid)
		var selection = Repository.GetConnectorByGuid(elemGuid);
		var client = Repository.GetElementByID(selection.ClientID)
		for (n=0; n<client.Connectors.Count; n++){
			if (client.Connectors.GetAt(n).Name == selection.Name){
				client.Connectors.Delete(n)
				client.Update()
				client.Connectors.Refresh()
			}
		}
	}
	Repository.RunModelSearch("View Candidates", "", "", "")

	Repository.SearchWindow.EnsureVisible()
}

main();

