function populateCustomTable() {

	// Define the SQL query
    var sqlQuery = `
        SELECT Capability.Object_ID, Capability.ea_guid AS CLASSGUID, Capability.Object_Type AS CLASSTYPE,
               Capability.Name AS Capability, BusinessValue.value AS BusinessValue, Category.value AS Category,
               ID.value AS ID, Owner.value AS Owner, Source.value AS Source, Increments.value AS Increments,
               IncrementsToBe.value AS IncrementsToBe, IncrementVertical.value AS IncrementVertical,
               IncrementSupplyChain.value AS IncrementSupplyChain, Cost.value AS Cost,
               Criticality.value AS Criticality, Risk.value AS Risk
        FROM t_object AS Capability
        INNER JOIN t_objectproperties AS BusinessValue ON (BusinessValue.Object_ID = Capability.Object_ID AND BusinessValue.Property = 'BusinessValue')
        INNER JOIN t_objectproperties AS Category ON (Category.Object_ID = Capability.Object_ID AND Category.Property = 'Category')
        INNER JOIN t_objectproperties AS ID ON (ID.Object_ID = Capability.Object_ID AND ID.Property = 'ID')
        INNER JOIN t_objectproperties AS Owner ON (Owner.Object_ID = Capability.Object_ID AND Owner.Property = 'Owner')
        INNER JOIN t_objectproperties AS Source ON (Source.Object_ID = Capability.Object_ID AND Source.Property = 'Source')
        INNER JOIN t_objectproperties AS Increments ON (Increments.Object_ID = Capability.Object_ID AND Increments.Property = 'Increments')
        INNER JOIN t_objectproperties AS IncrementsToBe ON (IncrementsToBe.Object_ID = Capability.Object_ID AND IncrementsToBe.Property = 'IncrementsToBe')
        INNER JOIN t_objectproperties AS IncrementVertical ON (IncrementVertical.Object_ID = Capability.Object_ID AND IncrementVertical.Property = 'IncrementVertical')
        INNER JOIN t_objectproperties AS IncrementSupplyChain ON (IncrementSupplyChain.Object_ID = Capability.Object_ID AND IncrementSupplyChain.Property = 'IncrementSupplyChain')
        INNER JOIN t_objectproperties AS Cost ON (Cost.Object_ID = Capability.Object_ID AND Cost.Property = 'Cost')
        INNER JOIN t_objectproperties AS Criticality ON (Criticality.Object_ID = Capability.Object_ID AND Criticality.Property = 'Criticality')
        INNER JOIN t_objectproperties AS Risk ON (Risk.Object_ID = Capability.Object_ID AND Risk.Property = 'Risk')
        WHERE Capability.stereotype = 'dCapability'
		AND Category.value = 'AiaB'
    `;

    // Execute the SQL query
    var result = Repository.SQLQuery(sqlQuery);


    // Parse the XML into a DOM structure
    var xmlDoc = XMLParseXML(result);

    // Extract nodes using XPath
    var objectIDNodes = XMLGetNodeTextArray(xmlDoc, "//Row/Increments");
    var capabilityNodes = XMLGetNodeTextArray(xmlDoc, "//Row/Capability");
    var businessValueNodes = XMLGetNodeTextArray(xmlDoc, "//Row/BusinessValue");
    var categoryNodes = XMLGetNodeTextArray(xmlDoc, "//Row/Risk");

    // Define the Custom Table Element ID
    var customTableElementID = 7691; // Replace with your actual ElementID

    // Retrieve the Custom Table element using its ElementID
    var customTable = Repository.GetElementByID(customTableElementID);
    if (!customTable) {
        Session.Output("Custom Table with ElementID " + customTableElementID + " not found.");
        return;
    }

    // Access the ElementGrid of the Custom Table
    var grid = customTable.GetElementGrid();
    if (!grid) {
        Session.Output("Failed to access the ElementGrid of the Custom Table.");
        return;
    }

    // Define column headers
    var columns = ["Maturity", "Capability", "BusinessValue", "Risk"];

    // Set the grid size: rows = number of results + 1 for header, columns = number of fields
    var rows = objectIDNodes.length + 1;
    var cols = columns.length;
    grid.SetGridSize(rows, cols);

    // Populate the header row
    for (var c = 0; c < cols; c++) {
        grid.SetCell(0, c, columns[c]);
    }

    // Populate the grid with data
    for (var r = 0; r < objectIDNodes.length; r++) {
        grid.SetCell(r + 1, 0, objectIDNodes[r]); // Object_ID
        grid.SetCell(r + 1, 1, capabilityNodes[r]); // Capability
        grid.SetCell(r + 1, 2, businessValueNodes[r]); // BusinessValue
        grid.SetCell(r + 1, 3, categoryNodes[r]); // Category
    }

    // Update the grid to apply changes
    grid.Update();

    // Refresh the diagram to display the updated table
    var diagram = Repository.GetCurrentDiagram();
    if (diagram) {
        Repository.ReloadDiagram(diagram.DiagramID);
    }

    Session.Output("Custom Table populated successfully.");
}

// Execute the function
populateCustomTable();


