function populateGridFromQuery(sqlQuery, columns, elementId) {
    // Execute the SQL query
    var result = Repository.SQLQuery(sqlQuery);

    // Parse the XML result
    var xmlDoc = XMLParseXML(result);
    if (!xmlDoc) {
        Session.Output("Failed to parse XML result");
        return false;
    }

    // Collect data for each column
    var columnData = [];
    for (var c = 0; c < columns.length; c++) {
        var nodes = XMLGetNodeTextArray(xmlDoc, columns[c].xpath);
        columnData.push(nodes);
    }

    // Validate we have data to populate
    if (columnData.length === 0 || columnData[0].length === 0) {
        Session.Output("No data found to populate grid");
        return false;
    }

    // Get the target element
    var customTable = Repository.GetElementByID(elementId);
    if (!customTable) {
        Session.Output("Element with ID " + elementId + " not found");
        return false;
    }

    // Access the element grid
    var grid = customTable.GetElementGrid();
    if (!grid) {
        Session.Output("Failed to access element grid");
        return false;
    }

    // Calculate grid dimensions
    var rowCount = columnData[0].length + 1; // +1 for header
    var colCount = columns.length;

    // Resize grid
    grid.SetGridSize(rowCount, colCount);

    // Set column headers
    for (var c = 0; c < colCount; c++) {
        grid.SetCell(0, c, columns[c].name);
    }

    // Populate data rows
    for (var r = 0; r < columnData[0].length; r++) {
        for (var c = 0; c < colCount; c++) {
            grid.SetCell(r + 1, c, columnData[c][r]);
        }
    }

    // Save changes
    grid.Update();

    // Refresh diagram if open
    var diagram = Repository.GetCurrentDiagram();
    if (diagram) {
        Repository.ReloadDiagram(diagram.DiagramID);
    }

    Session.Output("Grid populated successfully with " + columnData[0].length + " rows");
    return true;
}

