!INC TM Forum Normal Scripts.Custom Table - populate from query

function main() {
	   // Get current diagram
		var diagram = Repository.GetCurrentDiagram();
		if (!diagram) {
			Session.Output("Error: Please open a diagram containing the custom table");
			return;
		}

		// Get selected elements in the diagram
		var selectedElements = diagram.SelectedObjects;
		if (selectedElements.Count != 1) {
			Session.Output("Error: Please select exactly one custom table element in the diagram");
			return;
		}

		// Get the selected element
		var selectedElement = Repository.GetElementByID(selectedElements.GetAt(0).ElementID);
		if (!selectedElement) {
			Session.Output("Error: Failed to retrieve selected element");
			return;
		}

	// Define SQL query for specific business case
	var sqlQuery = `
					 SELECT Distinct Capability.Object_ID, Capability.ea_guid AS CLASSGUID, 
           Capability.Object_Type AS CLASSTYPE, Capability.Name AS Capability,
           BusinessValue.value AS BusinessValue, Risk.value AS Risk,
           Increments.value AS Increments
			FROM t_object AS Capability
			INNER JOIN t_objectproperties AS BusinessValue 
				ON (BusinessValue.Object_ID = Capability.Object_ID AND BusinessValue.Property = 'BusinessValue')
			INNER JOIN t_objectproperties AS Risk 
				ON (Risk.Object_ID = Capability.Object_ID AND Risk.Property = 'Risk')
			INNER JOIN t_objectproperties AS Increments 
				ON (Increments.Object_ID = Capability.Object_ID AND Increments.Property = 'Increments')
			INNER JOIN t_objectproperties AS Category 
				ON (Category.Object_ID = Capability.Object_ID AND Category.Property = 'Category')
			WHERE Capability.stereotype = 'dCapability'
			AND Category.value = 'AiaB'
	   `;

	// Define grid column configuration
		var gridColumns = [
			{ name: "Maturity",    xpath: "//Row/Increments" },
			{ name: "Capability",  xpath: "//Row/Capability" },
			{ name: "BusinessValue", xpath: "//Row/BusinessValue" },
			{ name: "Risk",       xpath: "//Row/Risk" }
		];

	// Specify target element ID
	var targetElementId = selectedElement.ElementID; 

	// Execute the population function
	populateGridFromQuery(sqlQuery, gridColumns, targetElementId);
}

main();

