!INC Local Scripts.EAConstants-JavaScript
!INC EAScriptLib.JavaScript-XML
!INC TM Forum Normal Scripts.Custom Table - populate from query

function main() {
	   // Get current diagram
		var diagram = Repository.GetCurrentDiagram();
		if (!diagram) {
			Session.Output("Error: Please open a diagram containing the custom table");
			return;
		}

		// Get selected elements in the diagram
		var selectedElements = diagram.SelectedObjects;
		if (selectedElements.Count != 1) {
			Session.Output("Error: Please select exactly one custom table element in the diagram");
			return;
		}

		// Get the selected element
		var selectedElement = Repository.GetElementByID(selectedElements.GetAt(0).ElementID);
		if (!selectedElement) {
			Session.Output("Error: Failed to retrieve selected element");
			return;
		}

	// Define SQL query for specific business case
	var sqlQuery = `
	   SELECT 
		 dMeasurementGrouping.Name AS [MeasurementGroup],
		AVG(CAST(CurrentLevel.Value AS SIGNED)) AS [Currenct], 
		AVG(CAST(SatisfactionLevel.Value AS SIGNED)) AS [Satisfacion]

	FROM 
		t_object AS KPI
	INNER JOIN 
		t_objectproperties AS CurrentLevel ON CurrentLevel.Object_ID = KPI.Object_ID
	INNER JOIN 
		t_objectproperties AS SatisfactionLevel ON SatisfactionLevel.Object_ID = KPI.Object_ID
	INNER JOIN 
		t_connector AS conn ON conn.Start_Object_ID = KPI.Object_ID
	INNER JOIN 
		t_object AS dMeasurementGrouping ON conn.End_Object_ID = dMeasurementGrouping.Object_ID
	WHERE 
		KPI.Stereotype = 'dMeasurementIndicator'
		AND SatisfactionLevel.Property = 'SatisfactionLevel'
		AND CurrentLevel.Property = 'CurrentLevel'
		AND dMeasurementGrouping.Stereotype = 'dMeasurementGrouping'
	GROUP BY 
		dMeasurementGrouping.Name
	ORDER BY 
		SUM(CAST(CurrentLevel.Value AS SIGNED)) DESC;

	   `;

	// Define grid column configuration
	var gridColumns = [
		{ name: "Measurement Group",    xpath: "//Row/MeasurementGroup" },
		{ name: "Currenct",  xpath: "//Row/Currenct" },
		{ name: "Satisfacion", xpath: "//Row/Satisfacion" }
	];

	// Specify target element ID
	var targetElementId = selectedElement.ElementID; 

	// Execute the population function
	populateGridFromQuery(sqlQuery, gridColumns, targetElementId);
}

main();

