!INC EAScriptLib.JavaScript-XML

function calculateComplexity() {
    Session.Output(" Calculating Complexity");

    // element IDs 
    var applicationsID = 8023;   // Impacted Applications
    var interfacesID   = 8033;   // Identified Interfaces
    var processesID    = 8066;   // Impacted Processes
    var rolesID        = 8051;   // Impacted Roles
    var complexityID   = 8065;   // Artemis Complexity

    // fetch elements 
    var appElem   = Repository.GetElementByID(applicationsID);
    var ifaceElem = Repository.GetElementByID(interfacesID);
    var procElem  = Repository.GetElementByID(processesID);
    var roleElem  = Repository.GetElementByID(rolesID);
    var compElem  = Repository.GetElementByID(complexityID);

    // parse their Name (which holds the counts) 
    var appsCount   = parseInt(appElem.Name,   10) || 0;
    var ifacesCount = parseInt(ifaceElem.Name, 10) || 0;
    var procsCount  = parseInt(procElem.Name,  10) || 0;
    var rolesCount  = parseInt(roleElem.Name,  10) || 0;

    // raw complexity = sum of all nodes + relationships 
    var complexityValue = (appsCount * ifacesCount)  + procsCount + rolesCount ;
    Session.Output("  Raw complexity = " + complexityValue);

    // write it back into the Complexity elements Name 
    compElem.Name = complexityValue.toString();
    compElem.Update();

    // refresh EAs tree so you see the new value 
   // Repository.RefreshModelView(appElem.PackageID);
    var diag = Repository.GetCurrentDiagram();
    if (diag) {
        Repository.ReloadDiagram(diag.DiagramID);
        Session.Output("Diagram refreshed.");
	}

    Session.Output(" Complexity updated on element ID " + complexityID);
}

/**
 * Resets the Name of each dashboard element to "processing"
 * so you can visually show counts are about to be recalculated.
 */


function resetDashboardProcessing() {
    Session.Output(" Resetting dashboard elements to 'processing'");


    // IDs of the elements to reset
    var elementIDs = [
        8023,  // Impacted Applications
        8033,  // Identified Interfaces
        8066,  // Impacted Processes
        8051,  // Impacted Roles
        8065   // Artemis Complexity
    ];

    // Loop and reset each one
    elementIDs.forEach(function(id) {
        var elem = Repository.GetElementByID(id);
        if (elem) {
            elem.Name = "processing";
            elem.Update();
            Session.Output("  Element  " + elem.alias + " set to 'processing'");
        }
        else {
            Session.Output("  Element ID " + elem.alias + " not found!");
        }
    });

    // Refresh the current diagram so the new names appear immediately
    var diag = Repository.GetCurrentDiagram();
    if (diag) {
        Repository.ReloadDiagram(diag.DiagramID);
        Session.Output("???? Diagram refreshed.");
    }

    Session.Output(" All dashboard elements reset.");
}


/**
 * Populates (in this case renames) a boundary element with the result of a single-row count query.
 *
 * @param {string} sqlQuery         - The SQL to execute (must return at least one row with a <Row><Value>count</Value></Row>).
 * @param {number} boundaryElementID - The EA.Element.ElementID of the boundary to update.
 */
function populateBoundaryFromQuery(sqlQuery, boundaryElementID) {
    Session.Output("=== Starting populateBoundaryFromQuery ===");

    // 1) Execute the SQL and get back EA's XML result
    var rawXml = Repository.SQLQuery(sqlQuery);
    if (!rawXml || rawXml.trim() === "") {
        Session.Output("No data returned from SQLQuery.");
        return;
    }

    // 2) Parse XML into a DOM
    var xmlDoc = XMLParseXML(rawXml);

    // 3) Extract the numeric count from each <Row>/<Value> node
    var counts = XMLGetNodeTextArray(xmlDoc, "//Row/Value");
    if (counts.length === 0) {
        Session.Output("Query returned zero rows.");
        return;
    }

    // 4) Retrieve the boundary element by its ElementID
    var boundaryElem = Repository.GetElementByID(boundaryElementID);
    if (!boundaryElem) {
        Session.Output("Boundary ElementID " + boundaryElementID + " not found.");
        return;
    }
    Session.Output("Located boundary element: " + boundaryElem.Name);

    // 5) Apply the first row's count as the new element Name
    //    (You could also set Notes or a TaggedValue instead, as needed.)
    boundaryElem.Name = counts[0];
    Session.Output("Boundary renamed to: " + boundaryElem.Name);

    // 6) Save and refresh
    boundaryElem.Update();
    var diag = Repository.GetCurrentDiagram();
    if (diag) {
        Repository.ReloadDiagram(diag.DiagramID);
        Session.Output("Diagram refreshed.");
    }
    Session.Output("=== populateBoundaryFromQuery complete ===");
}

// Execute the reset at the start of your dashboard script
resetDashboardProcessing();

