/*
 * Diagram Script main function
 */
function OnDiagramScript()
{
	// Get a reference to the current diagram
	var currentDiagram as EA.Diagram;
	currentDiagram = Repository.GetCurrentDiagram();

	if ( currentDiagram != null )
	{
		// Get a reference to any selected connector/objects
		var selectedConnector as EA.Connector;
		var selectedObjects as EA.Collection;
		selectedConnector = currentDiagram.SelectedConnector;
		selectedObjects = currentDiagram.SelectedObjects;

		if ( selectedConnector != null )
		{
			// A connector is selected
		}
		else if ( selectedObjects.Count > 0 )
		{
			// One or more diagram objects are selected
		}
		else
		{
			// Nothing is selected
		}
	}
	else
	{
		Session.Prompt( "This script requires a diagram to be visible.", promptOK)
	}
}

OnDiagramScript();

// Script Name:     HideConnectorLabelsExceptMultiplicity
// Author:          Giu Platania
// Purpose:         Hide all connector labels in the current diagram EXCEPT end multiplicities (source & target bottom labels).
// Date:            2025-08-13
// Version:         2.1 (fix regex escaping)

!INC Local Scripts.EAConstants-JScript

function log(msg) {
    var d = new Date();
    Session.Output((d.toISOString ? d.toISOString() : d.toString()) + " - " + msg);
}
function startLog() {
    Repository.CreateOutputTab("Script");
    Repository.ClearOutput("Script");
    Repository.EnsureOutputVisible("Script");
}

function setLabelHiddenFlag(geom, section, hide) {
    var visibleFlag = hide ? "1" : "0";

    // Match with optional leading '$' that EA sometimes prepends
    // Note: in string form for RegExp, backslashes must be doubled.
var re = new RegExp(section + "=([^;]*);", "i");
	log("Regex used: " + re);
    var m = re.exec(geom);
    if (m) {
        var before = m[0];
        var body = m[2] || "";
        var hadHDN = /(^|:)HDN=[0-9]/.test(body);
        if (hadHDN) {
            // flip HDN value only for this section
            var bodyNew = body.replace(/HDN=[0-9]/, "HDN=" + visibleFlag);
            var replaced = (m[1] || "") + section + "=" + bodyNew + ";";
            geom = geom.replace(before, replaced);
        } else {
            var addColon = body.length > 0 ? ":" : "";
            var replaced2 = (m[1] || "") + section + "=" + body + addColon + "HDN=" + visibleFlag + ";";
            geom = geom.replace(before, replaced2);
        }
    } else {
        // Append new section at the end
        geom += section + "=HDN=" + visibleFlag + ";";
    }
    return geom;
}

function hideSections(geom, arr) {
    for (var i = 0; i < arr.length; i++) geom = setLabelHiddenFlag(geom, arr[i], true);
    return geom;
}
function showSections(geom, arr) {
    for (var i = 0; i < arr.length; i++) geom = setLabelHiddenFlag(geom, arr[i], false);
    return geom;
}

startLog();
log("Starting: HideConnectorLabelsExceptMultiplicity v2.1");

var dgm = Repository.GetCurrentDiagram();
if (!dgm) {
    log("No active diagram. Open a diagram and run again.");
} else {
    log("Diagram: '" + dgm.Name + "' (ID=" + dgm.DiagramID + ") links=" + dgm.DiagramLinks.Count);
    Repository.SaveDiagram(dgm.DiagramID);

    for (var i = 0; i < dgm.DiagramLinks.Count; i++) {
        var dl = dgm.DiagramLinks.GetAt(i);
        var conn = null;
        try { conn = Repository.GetConnectorByID(dl.ConnectorID); } catch (e) {}

        log("----");
        log("Link #" + i + " ConnectorID=" + dl.ConnectorID + (conn ? " Type=" + conn.Type + " Name='" + conn.Name + "'" : ""));
        log("HiddenLabels flag (pre): " + dl.HiddenLabels);

        var g0 = dl.Geometry || "";
        log("Geometry (before): " + g0);

        var g1 = g0;
        g1 = hideSections(g1, ["LMT", "LMB", "LLT", "LRT"]);
        g1 = showSections(g1, ["LLB", "LRB"]);

        if (dl.HiddenLabels === true) {
            log("HiddenLabels is true -> setting to false");
            dl.HiddenLabels = false;
        }

        if (g1 !== g0) {
            dl.Geometry = g1;
            if (!dl.Update()) {
                log("Update FAILED: " + dl.GetLastError());
            } else {
                log("Update OK. Geometry (after): " + dl.Geometry);
            }
        } else {
            log("No geometry changes needed for this link.");
        }
    }

    Repository.ReloadDiagram(dgm.DiagramID);
    log("Reloaded diagram. Done.");
}
